(3, 'Website giá rẻ', '', 'website-gia-re', '', 'web giá rẻ là web giá rẻ', '<table border="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<table border="1" cellpadding="5" cellspacing="5" style="width:100%">\r\n				<tbody>\r\n					<tr>\r\n						<td><strong>STT</strong></td>\r\n						<td><strong>Chức năng</strong></td>\r\n						<td><strong>Diễn giải chức năng website</strong></td>\r\n					</tr>\r\n					<tr>\r\n						<td>1</td>\r\n						<td>Trang chủ</td>\r\n						<td>Giao diện ch&iacute;nh của website, được thiết kế v&agrave; tr&igrave;nh b&agrave;y theo phong c&aacute;ch của từng c&ocirc;ng ty, sao cho ph&ugrave; hợp với ng&agrave;nh nghề kinh doanh.</td>\r\n					</tr>\r\n					<tr>\r\n						<td>2</td>\r\n						<td>Trang giới thiệu</td>\r\n						<td>Trang c&oacute; chức năng cập nhật b&agrave;i viết v&agrave; h&igrave;nh ảnh giới thiệu về c&ocirc;ng ty v&agrave; sản phẩm dịch vụ kinh doanh.</td>\r\n					</tr>\r\n					<tr>\r\n						<td>3</td>\r\n						<td>Trang sản phẩm</td>\r\n						<td>Trang c&oacute; chức năng giới thiệu chi tiết h&igrave;nh ảnh của từng sản phẩm, liệt k&ecirc; sản phẩm theo cấp . Th&ocirc;ng tin về sản phẩm, chất lượng, gi&aacute; b&aacute;n,.v.v.v&hellip;Sản phẩm cập nhật th&ecirc;m kh&ocirc;ng hạn chế số lượng.</td>\r\n					</tr>\r\n					<tr>\r\n						<td>4</td>\r\n						<td>Trang tin tức</td>\r\n						<td>Trang c&oacute; chức năng đăng tải tin tức v&agrave; sự kiện của C&ocirc;ng ty, đồng thời đăng tải c&aacute;c tin tức li&ecirc;n quan</td>\r\n					</tr>\r\n					<tr>\r\n						<td>5</td>\r\n						<td>Trang li&ecirc;n hệ</td>\r\n						<td>Trang c&oacute; chức năng li&ecirc;n hệ giữa kh&aacute;ch h&agrave;ng v&agrave; c&ocirc;ng ty, th&ocirc;ng tin li&ecirc;n hệ th&ocirc;ng qua hệ thống website v&agrave; gửi về email của c&ocirc;ng ty. Trang được tr&igrave;nh b&agrave;y theo dạng from mặc định.</td>\r\n					</tr>\r\n					<tr>\r\n						<td>6</td>\r\n						<td>Chức năng tiện &iacute;ch</td>\r\n						<td>C&agrave;i chức năng thống k&ecirc; truy cập, c&agrave;i nick yahoo v&agrave;o hệ thống website.</td>\r\n					</tr>\r\n					<tr>\r\n						<td>7</td>\r\n						<td>Trang quản trị</td>\r\n						<td>Trang c&oacute; chức năng đăng nhập User name &amp; Password, c&oacute; 2 lớp. C&oacute; chức năng thay đổi Password của admin.</td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan="3"><strong>H&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để c&oacute; th&ecirc;m th&ocirc;ng tin chi tiết khuyến m&atilde;i Hosting 600MB</strong></td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1469765913, 1, 1, 5),
(4, 'Hosting 300MB', '', 'hosting-300mb', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="height:200px; width:200px">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;Băng th&ocirc;ng</td>\r\n			<td>5GB</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Email</td>\r\n			<td>10</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sub Domain</td>\r\n			<td>3</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PHP5, GD2</td>\r\n			<td>ok</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="text-align:center">35.000 vnđ/ th&aacute;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 1469766418, 1, 2, 9),
